  async function getClosedPullRequestInformation(repo, start, end, token) {
    const response = await fetch(`https://api.github.com/search/issues?q=repo:nationwide-building-society/${repo}+is:pr+closed:${start}..${end}&per_page=100`, {
        method: "GET",
        headers: {
            Authorization: `token ${token}`
        }
    });
    const result = await response.json().then(result => {return result});

    return result;
}

    async function getCreatedPullRequestInformation(repo, start, end, token) {
        const response = await fetch(`https://api.github.com/search/issues?q=repo:nationwide-building-society/${repo}+is:pr+created:${start}..${end}&per_page=100`, {
            method: "GET",
            headers: {
                Authorization: `token ${token}`
            }
        });

        const result = await response.json().then(result => {return result});

        return result;
    }

function getDifference(created, closed){
        const createDate = new Date(created);
        const closedDate = new Date(closed);
        const diffTime = Math.abs(closedDate - createDate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 

        return diffDays;
    }

function calculateAverage(array) {
    var total = 0;
    var count = 0;

    array.forEach(function(item, index){
        total += item;
        count++;
    });

    var result = total/count;


    return result.toFixed(2);
}    

function getAverageOpenTime(pullRequests){
    var newArr = [];
    pullRequests.forEach(pr => {
        var daysOpen = getDifference(pr.created_at, pr.closed_at);
        newArr.push(daysOpen);
    });

    return calculateAverage(newArr);
}    

function getClosedPRsNoMerge(pullRequests){
    var noMergeCount = 0;
    pullRequests.forEach(pr => {
        if (pr.pull_request.merged_at === null){
            noMergeCount++;
        }
    });

    return noMergeCount;
}

async function gatherData(repo, start, end, token){
    var closedInfo = await getClosedPullRequestInformation(repo, start, end, token);
    var createdInfo = await getCreatedPullRequestInformation(repo, start, end, token);

    var averageOpenTime = getAverageOpenTime(closedInfo.items);
    var closedNoMerge = getClosedPRsNoMerge(closedInfo.items);

   //accumulate data and put into custom data object to return and display
   var arr = {
       name: repo, 
       createdInfo: createdInfo, 
       closedInfo: closedInfo, 
       averageOpenTime: `${averageOpenTime} ${+ averageOpenTime == 1 ? 'day' : 'days'}`,
       closedNoMerge: closedNoMerge
    }


   return arr;
}

