import React from "react";

export default class FetchSpecificPullRequest extends React.Component {

    state = {
        loading : true,
        prInfo: null
    };

    async componentDidMount(){ 
        const prNumbers = this.props.prNumbers;

        const pullRequestInfo = [];

        prNumbers.forEach(async pr => {
            console.log('hi')

            const url = `https://api.github.com/repos/nationwide-building-society/${this.props.repo}/pulls/${pr.props.children}`;

            const response = await fetch(url, {
                method: "GET",
                headers: {
                    Authorization: `token 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`
                }
            });
            const data = await response.json();   
            console.log(data);
            pullRequestInfo.push(
                {title: data.title, created_at: data.created_at, url: data.url}
            );
        });

        this.setState({
            repo: this.props.repo,
            prInfo: pullRequestInfo,
            loading :false
        })

        const display = [];

        this.state.prInfo.forEach(info => {
            display.push(
                <ul>
            <li>{info.number}</li>
            <li>{info.number}</li>
            <li>{info.number}</li>
                </ul>

            );    
        });

        // console.log(data)


    }

    render(){
        return (this.state.prInfo)
    }

}
