//Passing in an array of repos, start and end date

//Rendering each FetchPullRequestData component


import React from "react";

export default class FetchOpenPullRequestData extends React.Component {
    state = {
        loading : true,
        repo: null,
        prNumbers: null
    };

    async componentDidMount(){
        const url = `https://api.github.com/repos/nationwide-building-society/${this.props.repo}/pulls?state=open&per_page=100`;
        const response = await fetch(url, {
            method: "GET",
            headers: {
                Authorization: `token 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`
            }
        });
        const data = await response.json();        
        const openPullRequests = [];

        data.forEach(pr => {
            openPullRequests.push(pr.number);    
        });

        console.log(openPullRequests)

        this.setState({
            repo: this.state.repo,
            loading :false
        })
    }

    render(){
        return (
            //For each pr number in array, display pr info
            <div>
                {this.state.prNumbers}
            </div>
        )

    }

}
