import React from "react";
import FetchSpecificPullRequest from "./FetchSpecificPullRequest";

export default class FetchOpenPullRequestData extends React.Component {
    state = {
        loading : true,
        repo: null,
        prNumbers: null,
        prInfo: null
    };



    async componentDidMount(){
        const url = `https://api.github.com/repos/nationwide-building-society/${this.props.repo}/pulls?state=open&per_page=100`;
        const response = await fetch(url, {
            method: "GET",
            headers: {
                Authorization: `token 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`
            }
        });
        const data = await response.json();        
        const openPullRequests = [];

        data.forEach(async pr => {
            await openPullRequests.push(pr.number);    
        });

        this.setState({
            repo: this.props.repo,
            prNumbers: await openPullRequests,
            loading :false
        })

        test();
    }

    async test(){
        const specificPrs = []
        this.state.prNumbers.forEach(async pr => {             
                await specificPrs.push(<FetchSpecificPullRequest repo={this.state.repo} prNumber={pr}/>) 
            })
  //  return specificPrs;
    this.setState(
        {
            prNumbers: specificPrs
        }
    )
        }

    render(){

//const specificPrInfo = this.test()

