    state = {
        loading : true,
        prNumbers: null,
        description: "",
        createdDate: null
    };

    async componentDidMount(){ 
        const prNumbers = this.props.prNumbers;

        const pullRequestInfo = [];

        prNumbers.forEach(pr => {
            const url = `https://api.github.com/repos/nationwide-building-society/${this.props.repo}/pulls/${pr}`;
            const response = fetch(url, {
                method: "GET",
                headers: {
                    Authorization: `token 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`
                }
            });
            const data = response.json();   

            pullRequestInfo.push(data);
        });

        console.log(pullRequestInfo);

        data.forEach(pr => {
            openPullRequests.push(
            <li>{pr.number}</li>
            );    
        });

        console.log(data)

        this.setState({
            repo: this.props.repo,
            prNumbers: openPullRequests,
            loading :false
        })
    }

