import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "./App.css";
import DisplayTitle from "./DisplayTitle.js";
import DisplayData from "./DisplayData";
import "react-datepicker/dist/react-datepicker.css";

function App() {
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const repoNames = ['nbs-lz-ami-bakery']
  const token = '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';

  const handleCheckInDate = (date) => {
    setCheckInDate(date);
    setCheckOutDate(null);
  };

  const handleCheckOutDate = (date) => {
    setCheckOutDate(date);
  };

  return (
    <div className="App">
      <div className="input-container">
        <div>
          <label>Start Date</label>
          <DatePicker
            selected={checkInDate}
            onChange={handleCheckInDate}
          />
        </div>
        <div>
          <label>End Date</label>
          <DatePicker
            selected={checkOutDate}
            minDate={checkInDate}
            maxDate={new Date()}
            onChange={handleCheckOutDate}
          />
        </div>
      </div>
      <div className="info">
      {checkInDate && checkOutDate && DisplayTitle(checkInDate.toISOString().slice(0, 10), checkOutDate.toISOString().slice(0, 10), repoNames, token)}
      <DisplayData />
      </div>

    </div>
  );
}

export default App;
