    const result = await response.json().then(result => {return result});

    return result;
}

    async function getCreatedPullRequestInformation(repo, start, end, token) {
        const response = await fetch(`https://api.github.com/search/issues?q=repo:nationwide-building-society/${repo}+is:pr+created:${start}..${end}&per_page=100`, {
            method: "GET",
            headers: {
                Authorization: `token ${token}`
            }
