import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "./App.css";
import DisplayTitle from "./DisplayTitle.js";
import moment from "moment";
import "react-datepicker/dist/react-datepicker.css";
import FetchPullRequestData from "./FetchPullRequestData";
import FetchOpenPullRequestData from "./FetchOpenPullRequestData";

function App() {
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const repoNames = ['nbs-lz-ami-bakery', 'nbs-lz', 'nbs-github-terraform', 'nbs-buildkite-terraform']
  const test = [];
  const openPrArray = [];


  const handleCheckInDate = (date) => {
    setCheckInDate(date);
    setCheckOutDate(null);
  };

  const handleCheckOutDate = (date) => {
    setCheckOutDate(date);
  };

  return (
    <div className="App">

<form>

        <div className="form-check">
          <label>
            <input
              type="radio"
              name="react-tips"
              value="option1"
              checked={true}
              className="form-check-input"
            />
            Option 1
          </label>
        </div>

        <div className="form-check">
          <label>
            <input
              type="radio"
              name="react-tips"
              value="option2"
              className="form-check-input"
            />
            Option 2
          </label>
        </div>

        <div className="form-check">
          <label>
            <input
              type="radio"
              name="react-tips"
              value="option3"
              className="form-check-input"
            />
            Option 3
          </label>
        </div>

        <div className="form-group">
          <button className="btn btn-primary mt-2" type="submit">
            Save
          </button>
        </div>

      </form>

      <h1>Pull Request Activity</h1>
      <div className="input-container">
        <div>
          <label>Start Date</label>
          <DatePicker
            selected={checkInDate}
            onChange={handleCheckInDate}
          />
        </div>
        <div>
          <label>End Date</label>
          <DatePicker
            selected={checkOutDate}
            minDate={checkInDate}
            maxDate={new Date()}
            onChange={handleCheckOutDate}
          />
        </div>
      </div>

      {/* <div className="info">
      {
      checkInDate && 
      checkOutDate && 

      //Use component that will display array of FetchPullRequestData components
        repoNames.forEach(repo => {
          test.push(<FetchPullRequestData repo={repo}start={moment(checkInDate).format("YYYY-MM-DD")} end={moment(checkOutDate).format("YYYY-MM-DD")}/>)
        })
      } */}

      {/* Display the array of pull request info. */}
      {/* {test}

      </div> */}


{/* New stuff below */}
{
  repoNames.forEach(repo => {
    openPrArray.push(<FetchOpenPullRequestData repo={repo}/>)
  })
}
{openPrArray}
    </div>
  );
}

export default App;
