//                 await specificPrs.push(<FetchSpecificPullRequest repo={this.state.repo} prNumber={pr}/>) 
//             })
//   //  return specificPrs;
//     this.setState(
//         {
//             prNumbers: specificPrs
//         }
//     )
//         }

    render(){

//const specificPrInfo = this.test()

        return (

            //For each pr number in array, display pr info
            !this.state.loading && this.state.prNumbers != null ? (

            <div>
                <h1>{this.state.repo} </h1>
                {/* ({this.state.prNumbers.length}) */}
               {
                this.state.prNumbers.map(mapping => (
                    <FetchSpecificPullRequest repo={this.state.repo} prNumber={mapping}/>
                ))
               } 
            </div>

            ) : (<p>"Loading"</p>)  
        )

    }

}
