import React, { useState } from "react";
import DatePicker from "react-datepicker";
import moment from "moment";

function Display(start, end, repos){



    return (  <div className="summary">
    <p>
    Pull Request Activity from {moment(start).format("LL")} to{" "} {moment(end).format("LL")}.
    </p>

    <div>
      {    
      repos.forEach(repo => {
        gatherData(repo).then(res => {
            console.log(res)
                display(res);     
        });
    })
    }
    </div>

  </div>)

  }


  async function getClosedPullRequestInformation(repo) {
    const response = await fetch(`https://api.github.com/search/issues?q=repo:nationwide-building-society/${repo}+is:pr+closed:${startDate}..${endDate}&per_page=100`, {
        method: "GET",
        headers: {
            Authorization: `token ${token}`
        }
    });
    const result = await response.json().then(result => {return result});

    return result;
}

    async function getCreatedPullRequestInformation(repo) {
        const response = await fetch(`https://api.github.com/search/issues?q=repo:nationwide-building-society/${repo}+is:pr+created:${startDate}..${endDate}&per_page=100`, {
            method: "GET",
            headers: {
                Authorization: `token ${token}`
            }
        });

        const result = await response.json().then(result => {return result});

        return result;
    }

function getDifference(created, closed){
        const createDate = new Date(created);
        const closedDate = new Date(closed);
        const diffTime = Math.abs(closedDate - createDate);
        const diffDays = Math.ceil(diffTime / (1000 * 60 * 60 * 24)); 

        return diffDays;
    }

function calculateAverage(array) {
    var total = 0;
    var count = 0;

    array.forEach(function(item, index){
        total += item;
        count++;
    });

    var result = total/count;


    return result.toFixed(2);
}    

function getAverageOpenTime(pullRequests){
    var newArr = [];
    pullRequests.forEach(pr => {
        var daysOpen = getDifference(pr.created_at, pr.closed_at);
        newArr.push(daysOpen);
    });

    return calculateAverage(newArr);
}    

function getClosedPRsNoMerge(pullRequests){
    var noMergeCount = 0;
    pullRequests.forEach(pr => {
        if (pr.pull_request.merged_at === null){
            noMergeCount++;
        }
    });

    return noMergeCount;
}

async function gatherData(repo){
    var closedInfo = await getClosedPullRequestInformation(repo);
    var createdInfo = await getCreatedPullRequestInformation(repo);

    var averageOpenTime = getAverageOpenTime(closedInfo.items);
    var closedNoMerge = getClosedPRsNoMerge(closedInfo.items);

   //accumulate data and put into custom data object to return and display
   var arr = {
       name: repo, 
       createdInfo: createdInfo, 
       closedInfo: closedInfo, 
       averageOpenTime: `${averageOpenTime} ${+ averageOpenTime == 1 ? 'day' : 'days'}`,
       closedNoMerge: closedNoMerge
    }

   return arr;
}








  export default Display;
