import React from "react";

export default class FetchPullRequestData extends React.Component {
    state = {
        loading : true,
        created: null, 
        closed: null, 
        average: null, 
        closedNoMerge: null, 
    };

    getAverageOpenTime(pullRequests){
        var newArr = [];
        pullRequests.forEach(pr => {
            var daysOpen = getDifference(pr.created_at, pr.closed_at);
            newArr.push(daysOpen);
        });

        return calculateAverage(newArr);
    }    

    getClosedPRsNoMerge(pullRequests){
        var noMergeCount = 0;
        pullRequests.forEach(pr => {
            if (pr.pull_request.merged_at === null){
                noMergeCount++;
            }
        });

        return noMergeCount;
    }



    async componentDidMount(){
        const url = `https://api.github.com/search/issues?q=repo:nationwide-building-society/nbs-github-terraform+is:pr+created:${this.props.start}..${this.props.end}&per_page=100`;
        const response = await fetch(url, {
            method: "GET",
            headers: {
                Authorization: `token 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`
            }
        });
        const data = await response.json();

        const url2 = `https://api.github.com/search/issues?q=repo:nationwide-building-society/nbs-github-terraform+is:pr+closed:${this.props.start}..${this.props.end}&per_page=100`;
        const response2 = await fetch(url2, {
            method: "GET",
            headers: {
                Authorization: `token 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`
            }
        });
        const closedData = await response2.json();

        const numberOfClosed = closedData.total_count;
        const numberOfCreated = createdData.total_count;
        const averageOpenTime = getAverageOpenTime(closedData.items);
        const closedNoMerge = this.getClosedPRsNoMerge(closedData.items);


        this.setState({
            created: numberOfCreated, 
            closed: numberOfClosed, 
            average: averageOpenTime, 
            closedNoMerge: closedNoMerge, 
            loading :false
        })

        console.log(data);
        console.log(data2);
    }

    render() {
        return (
            <div>
                {this.state.loading || !this.state.createdData || !this.state.closedData ? <div>loading...</div> : <div>{this.state.createdData} {this.state.closedData}</div>}
            </div>
        )
    }
}
