import React from "react";
import Alert from 'react-bootstrap/Alert';

export default class FetchSpecificPullRequest extends React.Component {

    state = {
        loading : true,
        prInfo: null
    };

    async componentDidMount(){ 


        const pullRequestInfo = [];



            const url = `https://api.github.com/repos/nationwide-building-society/${this.props.repo}/pulls/${this.props.prNumber}`;

            const response = await fetch(url, {
                method: "GET",
                headers: {
                    Authorization: `token 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`
                }
            });
            const data = await response.json();   

            const divStyle = {
                'textAlign': 'left'
              };

            const listStyle = {
                'listStyle': 'none'
              };


            //filtering out the open pull requests that have been approved and are ready for merging.
            if (data.mergeable_state == "clean") {

            pullRequestInfo.push(  
                <Alert style={divStyle} variant="outline-success">
                    <Alert.Heading>{data.title}</Alert.Heading>
                    {/* <ul style={listStyle}>
                        <li><strong>Created at:</strong> {data.created_at}</li>
                        <li><strong>URL:</strong> <a href={data.html_url}>{data.html_url}</a></li>
                    </ul> */}
                </Alert>
            );
            } else {
                pullRequestInfo.push(
                    <div style={divStyle}>
                        <h3>{data.title}</h3>
                        <ul style={listStyle}>
                            <li><strong>Created at:</strong> {data.created_at}</li>
                            <li><strong>URL:</strong> <a href={data.html_url}>{data.html_url}</a></li>
                        </ul>
                    </div>
                );
            }  

        this.setState({
            prInfo: await pullRequestInfo,
            loading :false
        })
    }

  render(){

return (
    this.state.prInfo
)
