import OpenPRContent from "./OpenPRContent";
import HistoricalPRContent from "./HistoricalPRContent";

function App() {
  const [checkInDate, setCheckInDate] = useState(null);
  const [checkOutDate, setCheckOutDate] = useState(null);
  const repoNames = ['nbs-lz-ami-bakery', 'nbs-lz', 'nbs-github-terraform', 'nbs-buildkite-terraform']
  const openPrArray = [];


  const handleCheckInDate = (date) => {
    setCheckInDate(date);
    setCheckOutDate(null);
  };

  const handleCheckOutDate = (date) => {
    setCheckOutDate(date);
  };


  const handleOptionChange = (changeEvent) => {
    this.setState({
      selectedOption: changeEvent.target.value
    });
  };

  const test = []

  return (


    <div className="App">

      <h1>Pull Request Activity</h1>
      <div className="input-container">
        <div>
          <label>Start Date</label>
          <DatePicker
            selected={checkInDate}
            onChange={handleCheckInDate}
          />
        </div>
        <div>
          <label>End Date</label>
          <DatePicker
            selected={checkOutDate}
            minDate={checkInDate}
            maxDate={new Date()}
            onChange={handleCheckOutDate}
          />
        </div>
      </div>

      <div className="info">
      {
      checkInDate && 
      checkOutDate && 

      //Use component that will display array of FetchPullRequestData components
        repoNames.forEach(repo => {
          test.push(<FetchPullRequestData repo={repo}start={moment(checkInDate).format("YYYY-MM-DD")} end={moment(checkOutDate).format("YYYY-MM-DD")}/>)
        })
      }

      {/* Display the array of pull request info. */}
      {test}

      </div>


{/* New stuff below */}
{
  repoNames.forEach(repo => {
    openPrArray.push(<FetchOpenPullRequestData repo={repo}/>)
  })
}
