import React, { useState } from "react";
import DatePicker from "react-datepicker";
import "./App.css";
import DisplayTitle from "./DisplayTitle.js";
import moment from "moment";
import "react-datepicker/dist/react-datepicker.css";
import FetchPullRequestData from "./FetchPullRequestData";
import FetchOpenPullRequestData from "./FetchOpenPullRequestData";


function App() {
  const [startDate, setStartDate] = useState(null);
  const [endDate, setEndDate] = useState(null);
  const repoNames = ['nbs-lz-ami-bakery', 'nbs-lz', 'nbs-github-terraform', 'nbs-buildkite-terraform']
  const openPrArray = [];

  const [isShown, setIsShown] = useState(false);

  const handleClick = (event) => {
      setIsShown(current => !current)
  }


  const handleStartDate = (date) => {
    setStartDate(date);
    setEndDate(null);
  };

  const handleEndDate = (date) => {
    setEndDate(date);
  };


  const handleOptionChange = (changeEvent) => {
    this.setState({
      selectedOption: changeEvent.target.value
    });
  };

  const test = []

  return (


    <div className="App">

    <button onClick={handleClick}>Show Pull Request Activity</button>

      <h1>Pull Request Activity</h1>
      <div className="input-container">
        <div>
          <label>Start Date</label>
          <DatePicker
            selected={startDate}
            onChange={handleStartDate}
          />
        </div>
        <div>
          <label>End Date</label>
          <DatePicker
            selected={endDate}
            minDate={startDate}
            maxDate={new Date()}
            onChange={handleEndDate}
          />
        </div>
      </div>

      <div className="info">
      {
      startDate && 
      endDate && 

      //Use component that will display array of FetchPullRequestData components
        repoNames.forEach(repo => {
          test.push(<FetchPullRequestData repo={repo}start={moment(startDate).format("YYYY-MM-DD")} end={moment(endDate).format("YYYY-MM-DD")}/>)
        })
      }

      {/* Display the array of pull request info. */}
      {test}

      </div>


{/* New stuff below */}
{
  repoNames.forEach(repo => {
    openPrArray.push(<FetchOpenPullRequestData repo={repo}/>)
  })
}
{openPrArray}
    </div>
  );
}

export default App;
